Bash(src/components/common/ExportButtons.jsx << 'EOF'
import { useState } from 'react';
import PropTypes from 'prop-types';
import { Download, Copy, Check } from 'lucide-react';
import { exportToCSV, copyToClipboard } from '../../utils/csvExport';

/**
 * Export buttons for calculator results
 */
export function ExportButtons\({ data, filename, summary }\) {
  const [copied, setCopied] = useState\(false\);

  const handleCopy = async \(\) => {
    const success = await copyToClipboard\(summary\);
    if \(success\) {
      setCopied\(true\);
      setTimeout\(\(\) => setCopied\(false\), 2000\);
    }
  };

  const handleExport = \(\) => {
    exportToCSV\(data, filename\);
  };

  return \(
    <div className="flex gap-3 flex-wrap">
      <button
        onClick={handleCopy}
        className="inline-flex items-center gap-2 px-4 py-2 bg-white dark:bg-slate-800 border-2 border-slate-200 dark:border-slate-700 text-slate-700 dark:text-slate-300 rounded-lg hover:border-accent dark:hover:border-accent transition-colors text-sm font-medium"
        title="Copy results to clipboard"
      >
        {copied ? \(
          <>
            <Check size={16} className="text-green-600" />
            Copied!
          </>
        \) : \(
          <>
            <Copy size={16} />
            Copy Results
          </>
        \)}
      </button>

      {data && data.length > 0 && \(
        <button
          onClick={handleExport}
          className="inline-flex items-center gap-2 px-4 py-2 bg-white dark:bg-slate-800 border-2 border-slate-200 dark:border-slate-700 text-slate-700 dark:text-slate-300 rounded-lg hover:border-accent dark:hover:border-accent transition-colors text-sm font-medium"
          title="Export data to CSV"
        >
          <Download size={16} />
          Export CSV
        </button>
      \)}
    </div>
  \);
}

ExportButtons.propTypes = {
  data: PropTypes.arrayOf\(PropTypes.object\),
  filename: PropTypes.string.isRequired,
  summary: PropTypes.string.isRequired,
};

export default ExportButtons;
EOF)

